def nbr_journaliste(tab)
	puts "le nombre de journaliste est : #{tab.length}"
	return tab.length
end

def handle_numero(tab)
	number=0
	n=0
	tab.each do |i|
		i.each_char do |j|
			if (j == '0' || j=='1' || j=='2' || j=='3' || j=='4' || j=='5' || j=='6' || j=='7' || j=='8' || j=='9')
				n=1
			end
		end
		if n==1
			number += n
		end
		n=0
	end
	puts "Parmi ces noms, #{number} contiennent des chiffres"
end

def prenom_aude(tab)
	nombre=0
	c = Array.new()
	tab.each do |i|
		p=0
		i.each_char do |j|
		c[p] = j
		p+=1
		end	
		for j in 0..c.length-3
			if (c[j]=='a' or c[j]=='A')
				if (c[j+1]=='u' or c[j+1]=='U')
					if (c[j+2]=='d' or c[j+2]=='D')
						if (c[j+3]=='e' or c[j+3]=='E')
							t=1
						end
					end
				end
			end
			if t==1
				nombre += t
				t=0
			end
		end
	end
puts "Le nombre de prenom qui contient AUDE est : #{nombre}"
end

def majuscule(tab)
	total=0
	j=1
	for i in 0..tab.length-1
		if (tab[i][j]=='A' || tab[i][j]=='B' || tab[i][j]=='C' || tab[i][j]=='D' || tab[i][j]=='E' || tab[i][j]=='F' || tab[i][j]=='G' || tab[i][j]=='H' || tab[i][j]=='I' || tab[i][j]=='J' || tab[i][j]=='K' || tab[i][j]=='L' || tab[i][j]=='M' || tab[i][j]=='N' || tab[i][j]=='O' || tab[i][j]=='P' || tab[i][j]=='Q' || tab[i][j]=='R' || tab[i][j]=='S' || tab[i][j]=='T' || tab[i][j]=='U' || tab[i][j]=='V' || tab[i][j]=='W' || tab[i][j]=='X' || tab[i][j]=='Y' || tab[i][j]=='Z')
			m=1
		end
		if m==1
