end

def majftsn(tab)
	nnn=0
	n1=0
	tab.each do |i|
		i.each_char do |j|
			if (j=='A' || j=='B' || j=='C' || j=='D' || j=='E' || j=='F' || j=='G' || j=='H' || j=='I' || j=='J' || j=='K' || j=='L' || j=='M' || j=='N' || j=='O' || j=='P' || j=='Q' || j=='R' || j=='S' || j=='T' || j=='U' || j=='V' || j=='W' || j=='X' || j=='Y' || j=='Z')
				n1=1
			end
		end
		if n1==1
			nnn += n1
		end
		n1=0
	end
	puts "Le nombre de prenom qui contient un majuscule est : #{nnn}"
end

def underscor(tab)
	num=0
	n2=0
	tab.each do |i|
		i.each_char do |j|
			if (j == '_')
				n2=1
			end
		end
		if n2==1
			num += n2
		end
		n2=0
	end
	puts "Le nombre de prenom qui contient un underscore est : #{num}"
end

